import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { Layout, Menu, theme } from 'antd';
import {
  DashboardOutlined,
  DesktopOutlined,
  AlertOutlined,
  HistoryOutlined,
  RobotOutlined,
  SettingOutlined,
  UserOutlined,
  ApiOutlined,
  CloudUploadOutlined,
  SafetyCertificateOutlined,
  ProjectOutlined,
  AuditOutlined,
  KeyOutlined,
  TeamOutlined,
  DatabaseOutlined,
  TableOutlined
} from '@ant-design/icons';
import Dashboard from './components/Dashboard';
import DeviceManagement from './components/DeviceManagement';
import AlertCenter from './components/AlertCenter';
import HistoryAnalysis from './components/HistoryAnalysis';
import AIAnalysis from './components/AIAnalysis';
import Settings from './components/Settings';
import Login from './components/Login';

// 新增的進階功能組件
import DeviceControl from './components/DeviceControl';
import OTAUpdate from './components/OTAUpdate';
import RuleEngine from './components/RuleEngine';
import WorkflowAutomation from './components/WorkflowAutomation';
import AuditTrail from './components/AuditTrail';
import RoleManagement from './components/RoleManagement';
import CommunicationProtocols from './components/CommunicationProtocols';
import DatabaseConnectionManagement from './components/DatabaseConnectionManagement';
import TableSchemaManagement from './components/TableSchemaManagement';
import './App.css';

const { Header, Sider, Content } = Layout;

function App() {
  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();

  const menuItems = [
    {
      key: '/',
      icon: <DashboardOutlined />,
      label: <Link to="/">儀表板</Link>,
    },
    {
      key: '/devices',
      icon: <DesktopOutlined />,
      label: <Link to="/devices">設備管理</Link>,
    },
    {
      key: '/device-control',
      icon: <ApiOutlined />,
      label: <Link to="/device-control">設備控制</Link>,
    },
    {
      key: '/ota-update',
      icon: <CloudUploadOutlined />,
      label: <Link to="/ota-update">OTA 更新</Link>,
    },
    {
      key: '/communication',
      icon: <ApiOutlined />,
      label: <Link to="/communication">通訊協定</Link>,
    },
    {
      key: '/alerts',
      icon: <AlertOutlined />,
      label: <Link to="/alerts">告警中心</Link>,
    },
    {
      key: '/history',
      icon: <HistoryOutlined />,
      label: <Link to="/history">歷史分析</Link>,
    },
    {
      key: '/ai',
      icon: <RobotOutlined />,
      label: <Link to="/ai">AI 分析</Link>,
    },
    {
      key: '/rule-engine',
      icon: <SafetyCertificateOutlined />,
      label: <Link to="/rule-engine">規則引擎</Link>,
    },
    {
      key: '/workflow',
      icon: <ProjectOutlined />,
      label: <Link to="/workflow">工作流程</Link>,
    },
    {
      key: '/audit-trail',
      icon: <AuditOutlined />,
      label: <Link to="/audit-trail">審計日誌</Link>,
    },
    {
      key: '/role-management',
      icon: <KeyOutlined />,
      label: <Link to="/role-management">角色管理</Link>,
    },
    {
      key: '/database-connections',
      icon: <DatabaseOutlined />,
      label: <Link to="/database-connections">資料庫連線</Link>,
    },
    {
      key: '/table-schemas',
      icon: <TableOutlined />,
      label: <Link to="/table-schemas">資料表設定</Link>,
    },
    {
      key: '/settings',
      icon: <SettingOutlined />,
      label: <Link to="/settings">系統設定</Link>,
    },
  ];

  return (
    <Router>
      <Layout style={{ minHeight: '100vh' }}>
        <Sider
          breakpoint="lg"
          collapsedWidth="0"
          style={{
            background: colorBgContainer,
          }}
        >
          <div style={{ 
            height: 32, 
            margin: 16, 
            background: 'rgba(255, 255, 255, 0.2)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#1890ff',
            fontWeight: 'bold',
            fontSize: '16px'
          }}>
            IIoT 平台
          </div>
          <Menu
            theme="light"
            mode="inline"
            defaultSelectedKeys={['/']}
            items={menuItems}
            style={{ borderRight: 0 }}
          />
        </Sider>
        <Layout>
          <Header
            style={{
              padding: 0,
              background: colorBgContainer,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              paddingLeft: 16,
              paddingRight: 16
            }}
          >
            <div style={{ fontSize: '18px', fontWeight: 'bold' }}>
              工廠物聯網平台
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
              <UserOutlined style={{ fontSize: '16px' }} />
              <span>管理員</span>
            </div>
          </Header>
          <Content
            style={{
              margin: '24px 16px',
              padding: 24,
              minHeight: 280,
              background: colorBgContainer,
              borderRadius: borderRadiusLG,
            }}
          >
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/devices" element={<DeviceManagement />} />
              <Route path="/device-control" element={<DeviceControl />} />
              <Route path="/ota-update" element={<OTAUpdate />} />
              <Route path="/communication" element={<CommunicationProtocols />} />
              <Route path="/alerts" element={<AlertCenter />} />
              <Route path="/history" element={<HistoryAnalysis />} />
              <Route path="/ai" element={<AIAnalysis />} />
              <Route path="/rule-engine" element={<RuleEngine />} />
              <Route path="/workflow" element={<WorkflowAutomation />} />
              <Route path="/audit-trail" element={<AuditTrail />} />
              <Route path="/role-management" element={<RoleManagement />} />
              <Route path="/database-connections" element={<DatabaseConnectionManagement />} />
              <Route path="/table-schemas" element={<TableSchemaManagement />} />
              <Route path="/settings" element={<Settings />} />
              <Route path="/login" element={<Login />} />
            </Routes>
          </Content>
        </Layout>
      </Layout>
    </Router>
  );
}

export default App;
